        FakeableHttpMessageHandler messageHandler = A.Fake<FakeableHttpMessageHandler>();

        A.CallTo(() => messageHandler.FakeSendAsync(
                A<HttpRequestMessage>.That.Matches(r => r.RequestUri.Host.Contains("google", StringComparison.OrdinalIgnoreCase))
                ,A<CancellationToken>.Ignored))
            .Returns(mockGoogleResponse);

        A.CallTo(() => messageHandler.FakeSendAsync(
                A<HttpRequestMessage>.That.Matches(r => r.RequestUri.Host.Contains("bing", StringComparison.OrdinalIgnoreCase))
